;\n}\n\n.file-upload-area[_ngcontent-%COMP%]:hover {\n  border-color: #e74c3c;\n  background: rgba(231, 76, 60, 0.05);\n}\n\n.upload-icon[_ngcontent-%COMP%] {\n  font-size: 2rem;\n  margin-bottom: 0.5rem;\n  color: #6c757d;\n}\n\n.file-upload-area[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n  margin: 0;\n  color: #6c757d;\n  font-size: 0.9rem;\n}\n\n\n\n.form-note[_ngcontent-%COMP%] {\n  display: block;\n  color: #6c757d;\n  font-size: 0.8rem;\n  margin-top: 0.25rem;\n  font-style: italic;\n}\n\n\n\n.form-actions[_ngcontent-%COMP%] {\n  margin-top: 2rem;\n  text-align: center;\n}\n\n.submit-btn[_ngcontent-%COMP%] {\n  background: #e74c3c;\n  color: white;\n  border: none;\n  padding: 1rem 3rem;\n  border-radius: 25px;\n  font-size: 1rem;\n  font-weight: 600;\n  cursor: pointer;\n  transition: all 0.3s ease;\n  box-shadow: 0 4px 15px rgba(231, 76, 60, 0.3);\n}\n\n.submit-btn[_ngcontent-%COMP%]:hover:not(:disabled) {\n  background: #c0392b;\n  transform: translateY(-2px);\n  box-shadow: 0 6px 20px rgba(231, 76, 60, 0.4);\n}\n\n.submit-btn[_ngcontent-%COMP%]:disabled {\n  background: #bdc3c7;\n  cursor: not-allowed;\n  transform: none;\n  box-shadow: none;\n}\n\n\n\n@media (max-width: 768px) {\n  .header[_ngcontent-%COMP%] {\n    padding: 1rem;\n  }\n  \n  .main-content[_ngcontent-%COMP%] {\n    padding: 1rem;\n  }\n  \n  .content-header[_ngcontent-%COMP%]   h1[_ngcontent-%COMP%] {\n    font-size: 2rem;\n  }\n  \n  .application-form[_ngcontent-%COMP%] {\n    padding: 1.5rem;\n  }\n  \n  .radio-group[_ngcontent-%COMP%] {\n    flex-direction: column;\n    gap: 0.75rem;\n  }\n  \n  .phone-input[_ngcontent-%COMP%] {\n    flex-direction: column;\n  }\n  \n  .country-code[_ngcontent-%COMP%] {\n    width: 100%;\n  }\n\n  \n\n  .sidebar[_ngcontent-%COMP%] {\n    width: 280px;\n  }\n  \n  .sidebar.active[_ngcontent-%COMP%] {\n    left: 0;\n  }\n}\n\n@media (max-width: 480px) {\n  .content-header[_ngcontent-%COMP%]   h1[_ngcontent-%COMP%] {\n    font-size: 1.8rem;\n  }\n  \n  .